function [B,T]=BOSC_tf_fft(eegsignal,F,Fsample,wavenumber)% [B,T,P]=BOSC_tf_fft(eegsignal,F,Fsample,wavenumber);%% This function computes a continuous wavelet (Morlet) transform on% a segment of EEG signal; this can be used to estimate the% background spectrum (BOSC_bgfit) or to apply the BOSC method to% detect oscillatory episodes in signal of interest (BOSC_detect).% this version has option uses frequency domain convolution%% parameters:% eegsignal - a row vector containing a segment of EEG signal to be%             transformed% F - a set of frequencies to sample (Hz)% Fsample - sampling rate of the time-domain signal (Hz)% wavenumber is the size of the wavelet (typically, width=6)%	% returns:% B - time-frequency spectrogram: power as a function of frequency%     (rows) and time (columns)st=1./(2*pi*(F/wavenumber));A=1./sqrt(st*sqrt(pi));B = zeros(length(F),length(eegsignal)); % initialize the time-frequency matrixfor f=1:length(F) % loop through sampled frequencies  t=-3.6*st(f):(1/Fsample):3.6*st(f);  m=A(f)*exp(-t.^2/(2*st(f)^2)).*exp(1i*2*pi*F(f).*t); % Morlet wavelet      Ly=length(eegsignal)+length(m)-1;  %     Ly2=pow2(nextpow2(Ly));    % Find smallest power of 2 that is > Ly    X=fft(eegsignal(:), Ly2);		   % Fast Fourier transform    H=fft(m(:), Ly2);	           % Fast Fourier transform    Y=X.*H;        	           %     y=ifft(Y, Ly2);      % Inverse fast Fourier transform    y=y(1:1:Ly);      y = y(floor(length(m)/2):end-ceil(length(m)/2)); %cut off the ends      B(f,:) = abs(y).^2;endT=(1:size(eegsignal,2))/Fsample;%    This file is part of the Better OSCillation detection (BOSC) library.%%    The BOSC library is free software: you can redistribute it and/or modify%    it under the terms of the GNU General Public License as published by%    the Free Software Foundation, either version 3 of the License, or%    (at your option) any later version.%%    The BOSC library is distributed in the hope that it will be useful,%    but WITHOUT ANY WARRANTY; without even the implied warranty of%    MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the%    GNU General Public License for more details.%%    You should have received a copy of the GNU General Public License%    along with Foobar.  If not, see <http://www.gnu.org/licenses/>.%%    Copyright 2010 Jeremy B. Caplan, Adam M. Hughes, Tara A. Whitten%    and Clayton T. Dickson.% Modifications made by K Mathewson 2016